ValidationRule.errorConditionFormula:
OR(
AND(
ISPICKVAL(PRIORVALUE(Status__c),"Cancelled"), NOT(ISPICKVAL(Status__c,"Cancelled"))
),
AND(
ISPICKVAL(PRIORVALUE(Status__c),"Rescheduled"), NOT(ISPICKVAL(Status__c,"Rescheduled"))
),

AND(
ISPICKVAL(PRIORVALUE(Status__c),"Completed"), NOT(ISPICKVAL(Status__c,"Completed")))

)
