ValidationRule.errorConditionFormula:
OR(
LEN( Customer_Phone__c ) <> 10, AND( NOT(BEGINS( Customer_Phone__c , '6')), NOT(BEGINS( Customer_Phone__c , '7')), NOT(BEGINS( Customer_Phone__c , '8')), NOT(BEGINS( Customer_Phone__c , '9'))
) ) && OR(LEN(Customer_Phone__c ) <> 13,NOT(BEGINS( Customer_Phone__c , '+91')))
